import React from 'react';

export function TodoInput({ value, onChange, onAdd }) {
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      onAdd();
    }
  };

  return (
    <div style={{ display: 'flex', gap: 8, marginBottom: 16 }}>
      <input
        type="text"
        value={value}
        onChange={(e) => onChange(e.target.value)}
        onKeyPress={handleKeyPress}
        placeholder="Add a new todo..."
        style={{
          flex: 1,
          padding: '8px 12px',
          border: '1px solid #ddd',
          borderRadius: 4,
          fontSize: 14
        }}
      />
      <button
        onClick={onAdd}
        style={{
          padding: '8px 16px',
          background: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: 4,
          cursor: 'pointer',
          fontSize: 14
        }}
      >
        Add
      </button>
    </div>
  );
}

import React from 'react';

export function TodoFilters({ filter, onFilterChange, counts, onClearCompleted }) {
  const { total, active, completed } = counts;

  if (total === 0) return null;

  return (
    <div style={{ display: 'flex', gap: 8, marginBottom: 16, borderBottom: '1px solid #eee', paddingBottom: 12 }}>
      <button
        onClick={() => onFilterChange('all')}
        style={{
          padding: '6px 12px',
          background: filter === 'all' ? '#007bff' : '#f8f9fa',
          color: filter === 'all' ? 'white' : '#333',
          border: '1px solid ' + (filter === 'all' ? '#007bff' : '#ddd'),
          borderRadius: 4,
          cursor: 'pointer',
          fontSize: 13
        }}
      >
        All ({total})
      </button>
      <button
        onClick={() => onFilterChange('active')}
        style={{
          padding: '6px 12px',
          background: filter === 'active' ? '#007bff' : '#f8f9fa',
          color: filter === 'active' ? 'white' : '#333',
          border: '1px solid ' + (filter === 'active' ? '#007bff' : '#ddd'),
          borderRadius: 4,
          cursor: 'pointer',
          fontSize: 13
        }}
      >
        Active ({active})
      </button>
      <button
        onClick={() => onFilterChange('completed')}
        style={{
          padding: '6px 12px',
          background: filter === 'completed' ? '#007bff' : '#f8f9fa',
          color: filter === 'completed' ? 'white' : '#333',
          border: '1px solid ' + (filter === 'completed' ? '#007bff' : '#ddd'),
          borderRadius: 4,
          cursor: 'pointer',
          fontSize: 13
        }}
      >
        Completed ({completed})
      </button>
      {completed > 0 && (
        <button
          onClick={onClearCompleted}
          style={{
            marginLeft: 'auto',
            padding: '6px 12px',
            background: '#dc3545',
            color: 'white',
            border: 'none',
            borderRadius: 4,
            cursor: 'pointer',
            fontSize: 13
          }}
        >
          Clear Completed
        </button>
      )}
    </div>
  );
}

import React from 'react';
import { TodoItem } from './TodoItem';

export function TodoList({ todos, onToggle, onDelete }) {
  if (todos.length === 0) {
    return (
      <p style={{ textAlign: 'center', color: '#999', marginTop: 32 }}>
        No todos to display.
      </p>
    );
  }

  return (
    <ul style={{ listStyle: 'none', padding: 0 }}>
      {todos.map(todo => (
        <TodoItem
          key={todo.id}
          todo={todo}
          onToggle={onToggle}
          onDelete={onDelete}
        />
      ))}
    </ul>
  );
}

import React from 'react';

export function TodoItem({ todo, onToggle, onDelete }) {
  return (
    <li
      style={{
        display: 'flex',
        alignItems: 'center',
        gap: 8,
        padding: '8px 12px',
        marginBottom: 8,
        background: '#f8f9fa',
        borderRadius: 4
      }}
    >
      <input
        type="checkbox"
        checked={todo.done}
        onChange={() => onToggle(todo.id)}
        style={{ cursor: 'pointer' }}
      />
      <span
        style={{
          flex: 1,
          textDecoration: todo.done ? 'line-through' : 'none',
          color: todo.done ? '#999' : '#000'
        }}
      >
        {todo.text}
      </span>
      <button
        onClick={() => onDelete(todo.id)}
        style={{
          padding: '4px 8px',
          background: '#dc3545',
          color: 'white',
          border: 'none',
          borderRadius: 4,
          cursor: 'pointer',
          fontSize: 12
        }}
      >
        Delete
      </button>
    </li>
  );
}

import React, { useState, useEffect } from 'react';
import { TodoInput } from './components/TodoInput';
import { TodoFilters } from './components/TodoFilters';
import { TodoList } from './components/TodoList';

const STORAGE_KEY = 'mini-lovable-todos';

export default function App() {
  const [todos, setTodos] = useState(() => {
    try {
      const saved = localStorage.getItem(STORAGE_KEY);
      return saved ? JSON.parse(saved) : [];
    } catch (error) {
      console.error('Failed to load todos:', error);
      return [];
    }
  });
  const [input, setInput] = useState('');
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    try {
      localStorage.setItem(STORAGE_KEY, JSON.stringify(todos));
    } catch (error) {
      console.error('Failed to save todos:', error);
    }
  }, [todos]);

  const addTodo = () => {
    if (input.trim()) {
      setTodos([...todos, { id: Date.now(), text: input, done: false }]);
      setInput('');
    }
  };

  const toggleTodo = (id) => {
    setTodos(todos.map(todo => 
      todo.id === id ? { ...todo, done: !todo.done } : todo
    ));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const clearCompleted = () => {
    setTodos(todos.filter(todo => !todo.done));
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.done;
    if (filter === 'completed') return todo.done;
    return true;
  });

  const activeCount = todos.filter(t => !t.done).length;
  const completedCount = todos.length - activeCount;

  return (
    <div style={{ padding: 24, maxWidth: 600, margin: '0 auto', fontFamily: 'system-ui' }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 24 }}>
        <h1 style={{ margin: 0 }}>Todo App</h1>
        <span style={{ fontSize: 12, color: '#666', background: '#f0f0f0', padding: '4px 8px', borderRadius: 4 }}>
          ðŸ’¾ Auto-saved â€¢ ðŸ“¦ Refactored
        </span>
      </div>

      <TodoInput
        value={input}
        onChange={setInput}
        onAdd={addTodo}
      />

      <TodoFilters
        filter={filter}
        onFilterChange={setFilter}
        counts={{
          total: todos.length,
          active: activeCount,
          completed: completedCount
        }}
        onClearCompleted={clearCompleted}
      />

      <TodoList
        todos={filteredTodos}
        onToggle={toggleTodo}
        onDelete={deleteTodo}
      />

      {todos.length === 0 && (
        <p style={{ textAlign: 'center', color: '#999', marginTop: 32 }}>
          No todos yet. Add one above!
        </p>
      )}
    </div>
  );
}

